import axios from 'axios';

const initialState = []

function getItemsReducer (state = initialState, action){
  switch(action.type){
    case 'INITIALIZE_ITEMS':
      axios.get('http://192.168.2.170:80/items')
      .then((response)=>{
        const itemDump = response.data.results;
        for(let index = 0; index < itemDump.length; index++){
          state.push({
            Key: itemDump[index].key,
            Name: itemDump[index].Name,
            Quantity: itemDump[index].Quantity,
            Category_Key:itemDump[index].Category_Key
          })
          };
          return state;
        })
    case 'GET_ITEMS':
      return state;
    default:
      return state;
  }

}



export default getItemsReducer;
