import React, {useState} from 'react';
import styled from 'styled-components';
import styledConstants from '../constants/styledConstants';
import {Alert, Image, StyleSheet, TextInput,Button, ScrollView, FlatList, TextComponent} from 'react-native';
import Menu from '../props/Menu';
import Item from '../props/Items'
import {useSelector, useDispatch} from 'react-redux';
import {getCategories, getItems} from '../actions';

const SearchItemTextInput = () => {
  return(
  <TextInput
    editable
    maxLength={10}
    placeholder = "Search"
  />
  );
}

const ChangeCategory = (props) => {
  return(
    <CategoriesContainer>
    <Text style = {{marginTop:5}}>{props.index}</Text>
  </CategoriesContainer>
  )
}

function Home (){
  const items = useSelector(item=>item.getItemsReducer)
  const dispatch =useDispatch()
  dispatch(getItems())
  let [categoryIndex, setIndex] = useState(0)
  let [tempOffset, setTempOffest] = useState(40)
  


  const ChangedIndex = (offset) =>{
    let tempIndex = parseFloat(offset / ((55*categoryIndex)+40))
    let index = parseInt(offset / ((55*categoryIndex)+40))
    console.log("OFFSET:  "+offset)
    console.log("tempINDEX: " +tempIndex)
    if (index ===1 && tempIndex >=parseFloat(1) ){
      setTempOffest(55*categoryIndex + 40)
      setIndex(categoryIndex + 1)
      console.log(categoryIndex)
      

    }
    else if (categoryIndex > 0 && offset < tempOffset){
      setIndex(categoryIndex - 1)
      setTempOffest(55*categoryIndex + 40)
      

    }
    console.log("AFTER:"+categoryIndex)
    console.log("INCREASE TEMP OFFSETAFTER: " + tempOffset)
    console.log("----------------")
  }

  return(      
    <Background>
      <Menu title='AppName' defaultMenu={true}></Menu>
       <ItemContentContainer>
        <SearchItemContainer>
          <SearchItemTextInput style = {{ height: 40, fontSize: 18 }}></SearchItemTextInput>
        </SearchItemContainer>
        <ChangeCategory index = {categoryIndex}/>
        <FlatList
          data = {items}
          keyExtractor = {(item) => item.Key}
          renderItem = {({item}) => {
            return(
              <Item Name = {item.Name} Quantity = {item.Quantity}/>
            )}
            }
          style={{width:"95%",height:"75%"}}
          onScroll={(e) => {
            ChangedIndex(e.nativeEvent.contentOffset.y)
          }}
        />
      </ItemContentContainer>
      <AddButtons>
        <Image style = {{backgroundColor:'white', width:25, height:25,left:8}} source={require('../assets/images/settings.svg')}/>
        <Image style = {{backgroundColor:'white', width:25, height:25,right:8}} source={require('../assets/images/settings.svg')}/>
        </AddButtons>

      
    </Background>
  );
}

export default Home;

const Background = styled.View`
  top:0;
  left:0;
  width: 100%;
  height: 100%;
  background-color: ${styledConstants.COLOR1};
`;
const Text = styled.Text``;
const ItemContentContainer = styled.View`
  margin-left:5%;
`;
const SearchItemContainer = styled.View`
  /* position: static; */
  border-width:1px;
  width:95%;
  border-radius:4px;
  margin-top:12px;
  /* margin-left:5%; */
`;
const CategoriesContainer = styled.View`
`

const AddButtons = styled.View`
  width: 100%;
  flex-direction:row;
  justify-content:space-between;
  align-items:center;
  margin-top:5px;
`;
