import axios from 'axios';
const categoryDic = [];

const getCategoriesReducer = (state = 0, action) => {
  switch(action.type){
    case 'INITIALIZE_CATEGORIES':
      axios.get('http://192.168.2.170:80/categories')
      .then((response)=>{
        const categoryDump = response.data.results;
        for(let index = 0; index < categoryDump.length; index++){
          state.push({
            Name: categoryDump[index].Name,
            key: categoryDump[index].Key
          })
        }
      })
      .catch((error) => {
        console.error(error);
      })
    case 'GET_CATEGORIES':
      return categoryDic;
  }
  return null;
}

export default getCategoriesReducer;
